import React, { useContext, useState } from "react";
import io from "socket.io-client";
import useSound from "use-sound";
import config from "../../../config";
import LatestMessagesContext from "../../../contexts/LatestMessages/LatestMessages";
import TypingMessage from "./TypingMessage";
import Header from "./Header";
import Footer from "./Footer";
import Message from "./Message";
import INITIAL_BOTTY_MESSAGE from "../../../common/constants/initialBottyMessage";
import "../styles/_messages.scss";

const socket = io(config.BOT_SERVER_ENDPOINT, {
  transports: ["websocket", "polling", "flashsocket"],
});

const ME = "me";
const BOT = "bot";

const INIT_MSG = {
  id: Date.now(),
  user: BOT,
  message: INITIAL_BOTTY_MESSAGE,
};

function Messages() {
  const [playSend] = useSound(config.SEND_AUDIO_URL);
  const [playReceive] = useSound(config.RECEIVE_AUDIO_URL);

  const { setLatestMessage } = useContext(LatestMessagesContext);

  const [message, setMessage] = useState("");

  const [messages, setMessages] = useState([INIT_MSG]);

  const [botTyping, setBotTyping] = useState(false);

  const sendMessage = (data) => {
    setLatestMessage(ME, data);
  };

  return (
    <div className="messages">
      <Header />
      <div className="messages__list" id="message-list"></div>
      {messages.map((msg, index) => (
        <Message
          nextMessage={messages[index + 1]}
          message={msg}
          botTyping={botTyping}
        />
      ))}

      <Footer
        message={message}
        sendMessage={() => sendMessage(message)}
        onChangeMessage={(e) =>
          setMessage({ ...message, message: e.target.value })
        }
      />
    </div>
  );
}

export default Messages;
